import React, { Component } from 'react';
import './todos.css';
import DAL from "../../DALUtils";
import {connect} from 'react-redux';
import Todo from "./Todo";

class Todos extends Component {
    constructor(props){
        super(props);
        this.state={todos:[]};
    }


    componentDidMount(){
        DAL.getUsers('https://jsonplaceholder.typicode.com/todos/').
        then(res => this.props.dispatch({type:'GET-ALL-TODOS' , todos:res.data})).
        catch(err => console.log(err));
    }

    static getDerivedStateFromProps(nextProps,nextState){
        console.log("Todos-sgetDerivedStateFromProps");
        return{todos:nextProps.data.todos}
    }



    render() {
        var todosMap= this.state.todos.map((item,index)=>{
            return<div className='todoMapping' key={index}>
                <div className='headlineDiv'>
                    <p>Todos - User {item.userId}</p>
                    <input type='button' value='Add' className='add-btn'/>
                </div>
                <div>
                    <Todo userId={item.id} title={item.title} completed={item.completed} />
                </div>
            </div>
        });

        return (
            <div id="Todos">
                {todosMap}
            </div>
        );
    }


}


const mapStateToProps = (state)=> {
    console.log("state todos",state);
    return {
        data: state
    }
};


export default connect(mapStateToProps)(Todos);
