import React, { Component } from 'react';
import DAL from '../DALUtils';

class FormsDemo extends Component {

    constructor(props){
        super(props);
        this.state = {isNameVaild:false , isEmailVaild: false};
    };

    changeColor=()=>{

    };

    existHeader=()=>{
        this.setState((currentState)=>{
            return {isExist: !currentState.isExist}
        })
    };

    getFormData=(e)=>{
        e.preventDefault();
        var fname = e.target['fname'].value;
        var email = e.target['email'].value;
        var city = e.target['city'].value;
        var obj={};
        if(fname != null && fname.length>0 && email != null && email.length>=4) {
            obj = {
                'name': fname,
                'email': email,
                'address': {'city': city}
            };
            DAL.addNewData('https://jsonplaceholder.typicode.com/users', obj).then(res => console.log(res.data)).catch(err => console.log(err));

        }else if(email != null && email.length>=4) {
                this.setState({isEmailVaild: true});

        }else if(fname != null && fname.length>0 ){
            this.setState({isNameVaild:false });
        }

    };

    render() {
        var nameEerrMsg;
        var emailEerrMsg;
        if(!this.state.isEmailVaild){
            nameEerrMsg = 'email need to be more then 4 letters'
        }
        if(!this.state.isNameVaild){
            emailEerrMsg = 'name is mandatory'
        }

        return (
            <div className="">
                <form onSubmit={this.getFormData}>
                    Name:<input type='text' name='fname'/>
                    <br/>
                    {nameEerrMsg}
                    Email:<input type='text' name='email'/>
                    <br/>
                    {emailEerrMsg}
                    address:
                    <br/>
                    city:
                    <select name='city'>
                        <option value="volvo">Hifa</option>
                        <option value="saab">Tel Aviv</option>
                        <option value="mercedes">Hadera</option>
                    </select>
                    <input type='submit' value='send'/>
                </form>
            </div>
        );
    }
}

export default FormsDemo;
