import {connect} from "react-redux";

const mainReducer = (
    state = {users:[], todos:[],posts:[],clickedUserId:"",usersDone:false,todosDone:false},  actionData) =>
{
    switch (actionData.type)
    {
        /***TODOS***/
        case 'GET-ALL-TODOS':{
            console.log("actionData todos-GET-ALL-TODOS",actionData);
            return Object.assign({},state,
                ...state.todos,
                {todos:actionData.todos,todosDone:true}
            );
        }

        case 'GET-TODOS':{
            console.log("GET-TODOS state.todos",state.todos);
            console.log("GET-TODOS actionData.todoId",actionData.todoId);
            console.log("actionData.users;",state.users);

            return Object.assign({},state,
                ...state.todos,
                {todos:state.todos ,todosDone:true}
            );
        }


        case 'ADD-TODO': {
            console.log("actionData",actionData);
            return Object.assign({},state,
                ...state.todos,
                {todos:state.todos}
            );
        }

        /***USERS***/
        case 'GET-ALL-USERS':{
            console.log('actionData-GET-ALL-USERS',actionData);
            return Object.assign({},state,
                ...state.users,
                {users:actionData.users ,usersDone:true,todos:state.todos}
            );
        }

        case 'GET-USERS':{
            console.log("state.users state.users state.users",state.users);
            return Object.assign({},state,
                ...state.users,
                {users:state.users ,usersDone:true}
            );
        }

        case 'ADD-USER':
        {
            var arr = state.userArr;
            arr.push(actionData.user);
            return{userArr:arr}
        }
        case 'DELETE-USER':{
            console.log("actionData",actionData);
            return Object.assign({},state,
                ...state.users,
                {users:actionData.usersArr}
            );
        }

        /***POSTS***/
        case 'GET-ALL-POSTS':{
            console.log("actionData posts-GET-ALL-POSTS",actionData);
            return Object.assign({},state,
                ...state.posts,
                {posts:actionData.posts}
            );

        }
        case 'ADD-POST': {
            console.log("actionData POST",actionData);
            console.log("POST-STATE",state.posts);
            return Object.assign({},state,
                ...state.posts,
                {posts:actionData.posts}
            );
        }

        default:
            return state
    }
};

export default mainReducer;
