import React, { Component } from 'react';
import './App.css';
import Users from "./Users/Users";
import MainTasks from "./MainTasks/mainTasks";
import {Switch,Route,Link} from 'react-router-dom';
import DAL from "./DALUtils";
import {connect} from "react-redux";

class App extends Component {

    constructor(props){
        super(props);
        this.state={users:[],todos:[],activeUser:"",usersDone:false,todosDone:false,clickedUserId:""};
    }

    static getDerivedStateFromProps(nextProps,nextState){
        console.log("nextPropsssssss",nextProps);
        console.log("nextStatessssss",nextState);
        if(nextProps!=nextState){
            return{usersDone:nextProps.data.usersDone , todosDone:nextProps.data.usersDone,clickedUserId:nextProps.data.clickedUserId}
        }

    }


    render() {
        console.log("this.state.usersDone",this.state.usersDone);
        console.log("this.state.todosDone",this.state.todosDone);
        return (
            <div className="App">
                {this.state.usersDone && this.state.todosDone ?   <Users/> : "loading"}
                <Switch>
                    <Route exsct path='/' component={Users}/>
                    <Route path='/tasks/:ID' component={MainTasks}/>
                </Switch>
            </div>
        );
    }


    componentDidMount(){
        DAL.getData('https://jsonplaceholder.typicode.com/users').
        then(res => this.props.dispatch({type:'GET-ALL-USERS' , users:res.data ,usersDone:res.data})).
        catch(err => console.log("errr",err));

        DAL.getData('https://jsonplaceholder.typicode.com/todos').
        then(res => this.props.dispatch({type:'GET-ALL-TODOS' , todos:res.data ,todosDone:res.data , clickedUserId:this.state.clickedUserId,})).
        catch(err => console.log(err));
    }




}


const mapStateToProps = (state)=> {
    console.log("state users",state);
    return {
        data: state
    }
};


export default connect(mapStateToProps)(App);
