import React, { Component } from 'react';
import ChildComp from "./ChildComp";

class ParentComp extends Component {
    constructor(props){
        super(props);
        this.state= {data: "hello" , newText:"" , persons:[]}
    }

    changeChildeData=()=>{
        this.setState({data:"no longer hello"});
    };

    getLiveText=(e)=>{
        var newText =  e.target.value;
        this.setState({newText:newText});
    };

    myCallback=(data)=>{
      var arr= this.state.persons;
      arr.push(data);
        this.setState({persons:arr});
    };

    render() {

        var text = this.state.persons.map((x , index)=>{
            return <li key={index}>{x.name} , {x.age} , {x.city} , {x.adult} </li>
        });

        return (
            <div className={''}>
                <h1>im Parent</h1>
                <input type="button" onClick={this.changeChildeData} value="change child data"/>
                <br/>
                <input type="text" onChange={this.getLiveText}/> change my child comp live text
                <ChildComp dataFromParennt={this.state.data} textFromParent={this.state.newText} getChildProps={this.myCallback} />
              <ul>
                  {text}
              </ul>
            </div>
        );
    }
}

export default ParentComp;
