import React, { Component } from 'react';


class StageThree extends Component {

    constructor(props){
        super(props);
        this.state={newObj:{city:'Hifa'},getDataFromStorage:{},newDataFromStorage:{}}
    }

    static getDerivedStateFromProps(nextProps){
        var getDataFromStorage = localStorage.getItem('myData');

    }

    componentDidMount(){}

    componentDidUpdate(){
        return true;
    }

    getCity=(e)=>{
        var chosenCity = e.target.value;
        var newObj={'city':chosenCity};
        this.setState({newObj:newObj});


    };
    saveStorage=()=>{
        console.log(this.state.newObj , 'my new obj');
        
        this.setState(set(this.state.newDataFromStorage,this.state.newObj));
        console.log(this.state.newDataFromStorage , 'data From Storage');

        this.setState({newDataFromStorage:this.state.getDataFromStorage});
            localStorage.push('myData',JSON.stringify(this.state.newObj));

        /*---console the data in the localStorage---*/
/*
        console.log('getDataFromStorage:', JSON.parse(this.state.newDataFromStorage));
*/
    };

    render() {

        return (
            <div className="">
                <p>Chose City</p><br/>
                <select name='city' onChange={this.getCity}>
                    <option value="Hifa">Hifa</option>
                    <option value="TLV">Tel Aviv</option>
                    <option value="Hadera">Hadera</option>
                </select>
                <br/>
                <input type='button' value='Next' onClick={this.saveStorage}/>
            </div>
        );
    }
}

export default StageThree;
