import React, { Component } from 'react';


class StageThree extends Component {

    constructor(props){
        super(props);
        this.state={newObj:{} , didChange:false}
    }

    componentDidMount(){}

    componentDidUpdate(){
        return true;
    }

    getCity=(e)=>{
      this.setState({didChange:true});
      var chosenCity = e.target.value;
      var newObj={'city':chosenCity};
      this.setState({newObj:newObj})
    };
    saveStorage=()=>{
        if(this.state.didChange == true){
            localStorage.setItem('myData',JSON.stringify(this.state.newObj));
        }else {
            var oldObj={'city':'Hifa'};
            localStorage.setItem('myData',JSON.stringify(oldObj));
        }

        /*---console the data in the localStorage---*/
         var getDataFromStorage = localStorage.getItem('myData');
         console.log('getDataFromStorage:', JSON.parse(getDataFromStorage));
    };

    render() {

        return (
            <div className="">
                <p>Chose City</p><br/>
                <select name='city' onChange={this.getCity}>
                    <option value="Hifa">Hifa</option>
                    <option value="TLV">Tel Aviv</option>
                    <option value="Hadera">Hadera</option>
                </select>
                <input type='button' onClick={this.saveStorage}/>
            </div>
        );
    }
}

export default StageThree;
