import React, { Component } from 'react';
import {connect} from 'react-redux';


class Comp1 extends Component {

    constructor(props){
        super(props);
        this.state={name:'', age:''}
    }

    increment=()=>{
        this.props.dispatch({type: 'INCREMENT' , delta:3})
    };

    decrement=()=>{
        this.props.dispatch({type: 'DECREMENT' , delta:3})
    };

    addName=(e)=>{
        var name = e.target.value;
        this.setState({name:name});

    };

    addAge=(e)=>{
        var age = e.target.value;
        this.setState({age:age});
    };

    save=()=>{
        console.log();
        this.props.dispatch({type:'USER' , user:{name:this.state.name , age:this.state.age}});
    };

    render() {

        return (
            <div className="">
                <input type='button' value='+' onClick={this.increment}/>
                <input type='button' value='-' onClick={this.decrement}/>
                <br/>
                name:<input type='text' onChange={this.addName}/><br/>
                age:<input type='number' onChange={this.addAge}/>
                <input type='button' value='Save' onClick={this.save}/>

            </div>
        );
    }
}

export default connect()(Comp1);
