import React, { Component } from 'react';


class PersonComp extends Component {

    constructor(props){
        super(props);
        this.state = {name: "guffi" , age:'5' , counter:"" , dataText:"" , nunmber:"4"};
    };

    changeName = () =>{
        this.setState(function (currentState , props) {
            return {name:'daniel',age:'4',counter: currentState.counter +1}
        },() =>{console.log(this.state.name)}
    )};

    addNumber =(e)=>{
        this.setState({number: e.target.value})
    };

    render() {
        return (
            <div className="App">
                <sapn>my name is: {this.state.name}</sapn>
                <sapn> and my age is: {this.state.age}</sapn><br/>
            <input type='button' value='change name' onClick={this.changeName}/>
                <br/>
                <span>my total is:{this.state.number}</span><br/>
            <input type='number'/>
            <input type='button' onClick={this.addNumber}/>

            </div>
        );
    }
}

export default PersonComp;
