import React, { Component } from 'react';


class PersonComp extends Component {

    constructor(props){
        super(props);
        this.state = {name: "guffi" , age:'5' , counter:"" , dataText:"" , number:"4" ,total: "" , newNumber:"" , firstText:""};
    };

    changeName = () =>{
        this.setState(function (currentState , props) {
            return {name:'daniel',age:'4',counter: currentState.counter +1}
        },() =>{console.log(this.state.name)}
    )};

    addNumber =(e)=>{
        this.setState({newNumber: e.target.value})
    };

    changeTotal=()=>{
        this.setState({total: this.state.number + this.state.newNumber })
    };

    getValueOnChange=(e)=>{
        var x = e.target.value;
        this.setState({firstText: x.target.value})
    };
    getValueOnChange2=(e)=>{
        var x = e.target.value;
        this.setState({sectText: x.target.value})
    };

    swapFunction=()=>{

    };
    
    render() {
        return (
            <div className="App">
                <span>my name is: {this.state.name}</span>
                <span> and my age is: {this.state.age}</span><br/>
            <input type='button' value='change name' onClick={this.changeName}/>
                <br/>
                <span>my total is:{this.state.number}</span><br/>
            <input type='number' onChange={this.addNumber} />
            <input type='button' value="add" onClick={this.changeTotal}/>
            <br/>

            <input type='text' onChange={this.getValueOnChange}/>
                <input type='text' onChange={this.getValueOnChange2}/>
                <input type='button' value="swap" onClick={this.swapFunction}/>
            <span>first text box value is {this.state.firstText}</span>
            <span>sec text box value is {this.state.sectText}</span>
            </div>
        );
    }
}

export default PersonComp;
