import React, { Component } from 'react';
import './dynamicRender.css';

class Repeater extends Component {

    constructor(props){
        super(props);
        this.state = {
            color:"",
            text:"",
            isExistRed:false,
            isExistBlue:false,
            isExistGreen:false,
            names:[
            {name:"ron",age:"30"},
            {name:"gal",age:"28"},
            {name:"ronit",age:"5"},
            {name:"rona",age:"70"}
            ]
        };
    };


    addName=()=>{
      var arr = this.state.names;
      arr.push({name:"yoav" , age:"20"});
      this.setState({name:arr});
    };

    countText=(e)=>{
        var text = e.target.value;
        this.setState({text:text})
    };

    showChosenColor=(e)=>{
        var color = e.target.value;
        var colorName = e.target.name;
        console.log(colorName);
        /*this.setState({color:color});*/
        this.setState({color:true});
        console.log(color);


    };

    render() {

        var names = this.state.names.map((x , index)=>{
        return <li key={index}>{x.name} , {x.age}</li>
        });

        var names2 = this.state.names.map((x , index)=>{
        return <tr key={index}>
            <td>{x.name}</td>
            <td>{x.age}</td>
            </tr>
        });

        var text = this.state.text;
        var textStyle;

        if(this.state.text.length > 5){
            textStyle = "redTextBox";
        }else{
            textStyle = "blueTextBox";
        }

        if(this.state.isExistRed === true || this.state.isExistBlue === true || this.state.isExistGreen === true){
            return  
        }

        return (
            <div className="repeater">
                <ul>
                    {names}
                </ul>

                <table style={{border:"1px solid"}}>
                    <tbody>{names2}</tbody>
                </table>
                <br/>
                <input type='button' value="add name" onClick={this.addName}/>
                <br/><br/>
                <span>change color if more then 5 letters</span>
                <input type="text" className={textStyle} onChange={this.countText}/>
                <br/><br/>
                <input type="checkbox" name="red"  value="isExistRed" onClick={this.showChosenColor}/>Red <br/>
                <input type="checkbox" name="blue" value="isExistBlue" onClick={this.showChosenColor}/> Blue <br/>
                <input type="checkbox" name="green" value="isExistGreen" onClick={this.showChosenColor}/>Green <br/>
                <ul>

                </ul>
            </div>
        );
    }
}

export default Repeater;
