import React, { Component } from 'react';
import axios from 'axios';

class RestDemo extends Component {
    constructor(props){
        super(props);
        this.state = {users:[] , user:{}}
    }

    getAll=()=>
    {
      axios.get('https://jsonplaceholder.typicode.com/users').
      then(res => this.setState({users : res.data})).
      catch(err => console.log(err));
    };


    getById=()=>{
        axios.get('https://jsonplaceholder.typicode.com/users/4').
        then(res => console.log(res.data)).
        catch(err => console.log(err));

    };

    getByIdByTextBox=(e)=>{
        var clickedId = e.target.value;

        axios.get('https://jsonplaceholder.typicode.com/users/' + clickedId ).
        then(res => this.setState({user : res.data})).
        catch(err => console.log(err));

    };


    addUser=()=>{
        var obj = {name:'avi' , age:'5'};

        axios.post('https://jsonplaceholder.typicode.com/users' , obj).
        then( res=>{console.log(res.data.id)}).
        catch(err => console.log(err));

    };

    updateUser=()=>{

        axios.put('https://jsonplaceholder.typicode.com/users/4').
        then( res=>{console.log(res.data.id)}).
        catch(err => console.log(err));

    };


    deleteUser=()=>{

        axios.delete('https://jsonplaceholder.typicode.com/users/5').
        then( res=>{console.log(res.data.id , 'deleted')}).
        catch(err => console.log(err));

    };




    render() {

        var users= this.state.users.map((item,index)=>{
           return <h3 key={index}> {item.name}</h3>
        });

       var user = this.props.user;

        return (
            <div className={''}>
                <input type="button" value="get all users" onClick={this.getAll}/><br/>
                <input type="button" value="get user to console" onClick={this.getById}/><br/>
                <input type="button" value="add user " onClick={this.addUser}/><br/>
                <input type="button" value="update user " onClick={this.updateUser}/><br/>
                <input type="button" value="delete user " onClick={this.deleteUser}/><br/>
                <br/>

                <input type="text" value="get user by id to console" onClick={this.getByIdByTextBox}/><br/>
                <input type="button" value="get user to console" onClick={this.getById}/><br/>


                {users}
                {user}

            </div>
        );
    }
}

export default RestDemo;
