import React, { Component } from 'react';
import {connect} from "react-redux";
import {withRouter} from "react-router-dom";
import {Switch,Route,Link} from 'react-router-dom';

class Add extends Component {
    constructor(props) {
        super(props);
        this.state = {title:"",body:""}
    }

    static getDerivedStateFromProps(nextProps, nextState) {
        console.log("nextProps", nextProps);
        console.log("nextState", nextState);
        return {urlID:nextProps.match.params.ID,urlPath:nextProps.location.pathname,todos:nextProps.data.todos}
    }

    componentDidMount(){}


    saveTitle=(e)=>{
        var title = e.target.value;
        this.setState({title:title});
    };

    saveBody=(e)=>{
        var body = e.target.value;
        this.setState({body:body});
    };

    cancel=()=>{
        this.props.history.push(`/tasks/${this.state.urlID}`);
    };

    addFor=()=>{
        if(this.state.urlPath === `/tasks/${this.state.urlID}/addTodo`){
            this.addTodo();
        }else if(this.state.urlPath === `/tasks/${this.state.urlID}/addPost`){
            this.addPost();
        }else{
            this.addUser();
        }
    };

    addTodo=()=>{
        var todos = this.state.todos;
        var last = todos.slice(-1).pop().id+1;
        var title = this.state.title;
        var urlID =parseInt(this.state.urlID);
        console.log("urlID",urlID);
        console.log("title",title);
        console.log("last",last);
        if(title.length > 0){
            todos.push({userId:urlID,id:last,title:title,completed:false});
            console.log("todos",todos);
            this.props.dispatch({type:'ADD-TODO' , todos});
        }



    };
    addPost=()=>{
        var posts = this.state.posts;
        var last = posts.slice(-1).pop().id+1;
        var title = this.state.title;
        var body = this.state.body;
        var urlID =parseInt(this.state.urlID);
        if(title.length > 0 && body.length > 0 ){
            posts.push({userId:urlID,id:last,title:title,body:body});
            console.log("posts",posts);
            this.props.dispatch({type:'ADD-TODO' , posts});
        }

    };
    addUser=()=>{};

    render() {

        return (
            <div className="addDiv">
                <div>
                    <p>title:</p><input type="text" onChange={this.saveTitle}/>
                    {this.state.urlPath  === `/tasks/${this.state.urlID}/addPost` &&
                    <div><p>body:</p><input type="text" onChange={this.saveBody}/></div>
                    }

                </div>
                <div className="btns">
                    <input type="button" value="Cancel" onClick={this.cancel}/>
                    <Link to={`/tasks/${this.state.urlID}`}><input type="button" value="Add" onClick={this.addFor}/></Link>
                </div>
            </div>
        )
    }
}

const mapStateToProps = (state)=> {
    console.log("mapStateToProps Add state",state);
    return {
        data: state
    }
};


export default withRouter(connect(mapStateToProps)(Add));
