import React, { Component } from 'react';
import axios from "axios";
import './users.css';
import User from "./User";


class Users extends Component {
    constructor(props){
        super(props);
        this.state={users:[]};
    }

    shouldComponentUpdate(nextProps , nextState){
        console.log(nextProps,"nextProps");
        console.log(nextState ,"nextState");
        return true;
    }

    componentDidMount(){
        axios.get('https://jsonplaceholder.typicode.com/users').
        then(res => this.setState({users : res.data})).
        catch(err => console.log(err));
        this.setState({users:users});


    }

    updateUser =(thisUserId,NewUserName,NewUserEmail,NewUserStreet,NewUserCity,NewUserZipcode)=>{
        var newUsers = this.state.users;

        var thisUserId = thisUserId;
        var NewUserName = NewUserName;
        var NewUserEmail = NewUserEmail;
        var NewUserStreet = NewUserStreet;
        var NewUserCity = NewUserCity;
        var NewUserZipcode = NewUserZipcode;

        for (var i in newUsers) {
            if (newUsers[i].id == thisUserId) {
                console.log('innnnn');
                newUsers[i].name = NewUserName;
                newUsers[i].email = NewUserEmail;
                newUsers[i].address.street = NewUserStreet;
                newUsers[i].address.city = NewUserCity;
                newUsers[i].address.zipcode = NewUserZipcode;
                break; //Stop this loop, we found it!
            }
        }

        console.log('new users arr:' , newUsers);
        this.setState({usersMap:newUsers});
        console.log("state users:", this.state.usersMap);
    };

    deleteUser=(thisUserId)=>{
        var thisUserId = thisUserId;
        var newUsers = this.state.users;
        newUsers.splice(thisUserId -1,1);
        console.log(thisUserId,"thisUserId");
        this.setState({users:newUsers});
        console.log("usersMap",this.state.users);
      /*  for (var i=0; i < newUsers.length-1; i++)
        {
            if (newUsers[i].id === thisUserId) {
                console.log(i , "i !!!!");
                console.log(newUsers[i].id);
                newUsers.splice(i,1);
                newArr.push(newUsers);
                console.log('newArr',newArr);
                this.setState({usersMap:newArr});
                console.log(this.state.usersMap , 'map');
            }
        }*/



    };


    render() {
      var usersMap= this.state.users.map((item,index)=>{
            return<div  key={index}>
                <User id={item.id} name={item.name} email={item.email} street={item.address.street} city={item.address.city} zipcode={item.address.zipcode} onUpdate={this.updateUser} onDelete={this.deleteUser}/>
            </div>
        });

        return (
            <div id="Users">
                {usersMap}
            </div>
        );
    }
}

export default Users;
