import React, { Component } from 'react';
import axios from "axios";



class User extends Component {
    constructor(props){
        super(props);
        this.state={ userId:this.props.userId, name: this.props.name, email: this.props.email, street:this.props.street, city:this.props.city, zipcode:this.props.zipcode , index:this.props.index};
    }

    static getDerivedStateFromProps(nextProps,nextState){
        console.log(nextProps, "nextProps" );
        console.log(nextState, "nextState" );
        if(nextState != nextProps){
       this.setState({userId:nextProps.id});
        }
        return {}
    }

    componentDidMount(){}

    active=()=>{
        if( this.state.active.length <= 0){
            this.setState({active:"active"});
        }else{
            this.setState({active:""});
        }
    };

    showOtherDataOnHover=()=>{
        this.setState({active:"active"});
    };

    updateUserDetails=(e)=>{
        var inputName = e.target.name;
        console.log( e.target.name);
        var e = e.target.value;
        switch (inputName){
            case 'name':
                this.setState({name:e});
                break;
            case 'email':
                this.setState({email:e});
                break;
            case 'street':
                this.setState({street:e});
                break;
            case 'city':
                this.setState({city:e});
                break;
            case 'zipcode':
                this.setState({zipcode:e});
                break;
            default:
                console.log("not matched");

        }
    };

    updateUserInArray=()=>{
        console.log('index',this.state.index);
        var thisUserId = this.props.userId;
        var NewUserName = this.state.name;
        var NewUserEmail = this.state.email;
        var NewUserStreet = this.state.street;
        var NewUserCity = this.state.city;
        var NewUserZipcode = this.state.zipcode;

        this.props.onUpdate(thisUserId,NewUserName,NewUserEmail,NewUserStreet,NewUserCity,NewUserZipcode)
    };

    deleteUserInArray=()=>{
        var thisUserId = this.props.userId;
        this.props.onDelete(thisUserId);

    };

    render() {
        console.log(this.state.userId , "in user this is id");
        return (
            <div id="User">
                <p>ID: {this.state.userId}</p>
                Name: <input type='test' name="name" value={this.state.name} onChange={this.updateUserDetails}/><br/>
                Email: <input type='text' name="email" value={this.state.email} onChange={this.updateUserDetails}/><br/>
                <input  type='button' className='user-btn btn-other' value='Other Data' onMouseEnter={this.showOtherDataOnHover} onClick={this.active}  />
                <div id='otherData' className={this.state.active}>
                    Street: <input type='text' name="street" value={this.state.street} onChange={this.updateUserDetails}/><br/>
                    City: <input type='text' name="city" value={this.state.city} onChange={this.updateUserDetails}/><br/>
                    Zipcode: <input type='text' name="zipcode" value={this.state.zipcode} onChange={this.updateUserDetails}/><br/>
                </div>
                <input  type='button' className='user-btn btn-update' value='Update' onClick={this.updateUserInArray}/>
                <input  type='button' className='user-btn btn-delete' value='Delete' onClick={this.deleteUserInArray}/>
            </div>
        );
    }
}

export default User;
