import React, { Component } from 'react';
import axios from 'axios';

class FormsDemo extends Component {

    constructor(props){
        super(props);
        this.state = {isNameVaild:false , isEmailVaild: false};
    };

    changeColor=()=>{

    };

    existHeader=()=>{
        this.setState((currentState)=>{
            return {isExist: !currentState.isExist}
        })
    };

    getFormData=(e)=>{
        e.preventDefault();
        var fname = e.target['fname'].value;
        var email = e.target['email'].value;
        var city = e.target['city'].value;

        if(fname != null && fname.length>0) {
            var obj = {name: fname};
            this.setState({isNameVaild: true})
        }
        if(email != null && fname.length>=4) {
            var obj = {
                email: email,
                city: city
            };
            this.setState({isEmailVaild: true})
        }
    };

    render() {
        var errMsg;
        if(!this.state.isEmailVaild){
            errMsg = 'email need to be more then 4 letters'
        }
        if(!this.state.isNameVaild){
            errMsg = 'name is mandatory'
        }

        return (
            <div className="">
                <form onSubmit={this.getFormData}>
                    Name:<input type='text' name='fname'/>
                    <br/>
                    Email:<input type='text' name='email'/>
                    <br/>
                    address:
                    <br/>
                    city:
                    <select name='city'>
                        <option value="volvo">Hifa</option>
                        <option value="saab">Tel Aviv</option>
                        <option value="mercedes">Hadera</option>
                    </select>
                    <input type='submit' value='send'/>
                </form>
                {errMsg}
            </div>
        );
    }
}

export default FormsDemo;
