import React, { Component } from 'react';
import axios from "axios";



class Todo extends Component {
    constructor(props){
        super(props);
        this.state={ userId:this.props.userId, title: this.props.title, completed: this.props.completed};
    }

    static getDerivedStateFromProps(nextProps,nextState){
        console.log("user-getDerivedStateFromProps");
        if(nextState.userId != nextProps.userId){
            return{ userId:nextProps.userId, title:nextProps.title, completed:nextProps.completed}
        }else{
            return{ userId:nextState.userId, title:nextState.title, completed:nextState.completed}
        }
    }

    componentDidMount(){}

    active=()=>{
        if( this.state.active.length <= 0){
            this.setState({active:"active"});
        }else{
            this.setState({active:""});
        }
    };

    showOtherDataOnHover=()=>{
        this.setState({active:"active"});
    };

    updateUserDetails=(e)=>{
        var inputName = e.target.name;
        var e = e.target.value;
        switch (inputName){
            case 'name':
                console.log(e, "e");
                this.setState({name:e});
                break;
            case 'email':
                this.setState({email:e});
                break;
            case 'street':
                this.setState({street:e});
                break;
            case 'city':
                this.setState({city:e});
                break;
            case 'zipcode':
                this.setState({zipcode:e});
                break;
            default:
                console.log("not matched");

        }
    };

    updateUserInArray=()=>{
        var thisUserId = this.props.userId;
        var NewUserName = this.state.name;
        var NewUserEmail = this.state.email;
        var NewUserStreet = this.state.street;
        var NewUserCity = this.state.city;
        var NewUserZipcode = this.state.zipcode;

        this.props.onUpdate(thisUserId,NewUserName,NewUserEmail,NewUserStreet,NewUserCity,NewUserZipcode)
    };

    deleteUserInArray=()=>{
        var thisUserId = this.props.userId;
        this.props.onDelete(thisUserId);

    };

    render() {
        return (
            <div id="Todo">
                <p>title: {this.state.title}</p><br/>
                <p>completed: {this.state.completed}</p><br/>
                { this.state.completed ?  <input  type='button' className='user-btn btn-completed' value='Mark Completed' onClick={this.deleteUserInArray}/> : null }
            </div>
        );
    }
}

export default Todo;
