import React, { Component } from 'react';
import axios from "axios";
import './users.css';
import User from "./User";


class Users extends Component {
    constructor(props){
        super(props);
        this.state={users:[] , usersMap:[]};
    }

   /* shouldComponentUpdate(nextProps , nextState){
        console.log(nextProps,"nextProps");
        console.log(nextState ,"nextState");
        return false;
    }
*/
    componentDidMount(){
        axios.get('https://jsonplaceholder.typicode.com/users').
        then(res => this.setState({users : res.data})).
        catch(err => console.log(err));



    }

    updateUser =(thisUserId,NewUserName,NewUserEmail,NewUserStreet,NewUserCity,NewUserZipcode)=>{
        var newUsers = this.state.users;

        var thisUserId = thisUserId;
        var NewUserName = NewUserName;
        var NewUserEmail = NewUserEmail;
        var NewUserStreet = NewUserStreet;
        var NewUserCity = NewUserCity;
        var NewUserZipcode = NewUserZipcode;

        for (var i in newUsers) {
            if (newUsers[i].id == thisUserId) {
                console.log('innnnn');
                newUsers[i].name = NewUserName;
                newUsers[i].email = NewUserEmail;
                newUsers[i].address.street = NewUserStreet;
                newUsers[i].address.city = NewUserCity;
                newUsers[i].address.zipcode = NewUserZipcode;
                break; //Stop this loop, we found it!
            }
        }

        console.log('new users arr:' , newUsers);
        this.setState({usersMap:newUsers});
        console.log("state users:", this.state.usersMap);
    };

    deleteUser=(userId)=>{

        var userId = userId;
        var usersArr= this.state.users;
        var found = usersArr.find(function(x) {
            return x.id === userId ;
        });
        console.log(found);
        var index2 = usersArr.indexOf(found);
        console.log(index2);
        var removed = usersArr.splice(index2, 1);
        this.setState({users:usersArr});
        console.log(removed,"removed");
        console.log(this.state.users);

    };


    render() {
        console.log(this.state.users , "rrrrrrrrrrr");
        var usersMap= this.state.users.map((item,index)=>{
            return<div className='usrsMapping' key={index}>
                <User userId={item.id} name={item.name} email={item.email} street={item.address.street} city={item.address.city} zipcode={item.address.zipcode} onUpdate={this.updateUser} onDelete={this.deleteUser}/>
            </div>
        });

        return (
            <div id="Users">
                {usersMap}
            </div>
        );
    }
}

export default Users;
