import React, { Component } from 'react';


class StageThree extends Component {

    constructor(props){
        super(props);
        this.state={newObj:{},dataFromStorage:{} ,didChange:false}
    }

    componentDidMount(){}

    componentDidUpdate(){
        var getDataFromStorage = localStorage.getItem('myData');
        this.setState({dataFromStorage:getDataFromStorage});

        /*return true;*/
    }

    getCity=(e)=>{
        this.setState({didChange:true});
        var chosenCity = e.target.value;
        var newObj={'city':chosenCity};
        this.setState({newObj:newObj} , function () {
            console.log(this.state.newObj , 'my new obj');
            this.state.dataFromStorage.push(this.state.newObj);
            console.log(this.state.dataFromStorage , 'data From Storage');
        });


    };
    saveStorage=()=>{

        if(this.state.didChange == true){

            localStorage.posh('myData',JSON.stringify(this.state.newObj));
        }else {
            var oldObj={'city':'Hifa'};
            localStorage.setItem('myData',JSON.stringify(oldObj));
        }

        /*---console the data in the localStorage---*/
/*
        console.log('getDataFromStorage:', JSON.parse(this.state.dataFromStorage));
*/
    };

    render() {

        return (
            <div className="">
                <p>Chose City</p><br/>
                <select name='city' onChange={this.getCity}>
                    <option value="Hifa">Hifa</option>
                    <option value="TLV">Tel Aviv</option>
                    <option value="Hadera">Hadera</option>
                </select>
                <input type='button' onClick={this.saveStorage}/>
            </div>
        );
    }
}

export default StageThree;
