import React, { Component } from 'react';
import './todos.css';
import DAL from "../DALUtils";
import {connect} from 'react-redux';
import Todo from "./Todo";

class Todos extends Component {
    constructor(props){
        super(props);
        this.state={todos:[]};
    }


    componentDidMount(){
        DAL.getUsers('https://jsonplaceholder.typicode.com/todos').
        then(res => this.props.dispatch({type:'GET-ALL-TODOS' , todos:res.data})).
        catch(err => console.log(err));
    }

    static getDerivedStateFromProps(nextProps,nextState){
        console.log("Todos-sgetDerivedStateFromProps");
        return{todos:nextProps.data.todos}
    }


    updateUser =(newId,newName,newEmail,newStreet,newCity,newZipcode)=>{
        var newUsers = this.state.users;
        var thisUserId = newId;
        var NewUserName = newName;
        var NewUserEmail = newEmail;
        var NewUserStreet = newStreet;
        var NewUserCity = newCity;
        var NewUserZipcode = newZipcode;

        for (var i in newUsers) {
            if (newUsers[i].id == thisUserId) {
                newUsers[i].name = NewUserName;
                newUsers[i].email = NewUserEmail;
                newUsers[i].address.street = NewUserStreet;
                newUsers[i].address.city = NewUserCity;
                newUsers[i].address.zipcode = NewUserZipcode;
                break; //Stop this loop, we found it!
            }
        }

        console.log('new users arr:' , newUsers);
        this.setState({usersMap:newUsers});
        console.log("state users:", this.state.usersMap);
    };

    deleteUser=(userId)=>{

        var userId = userId;
        var usersArr= this.state.users;
        var found = usersArr.find(function(x) {
            return x.id === userId ;
        });
        var index2 = usersArr.indexOf(found);
        usersArr.splice(index2, 1);
        this.setState({users:usersArr});
    };


    render() {
        console.log(this.state.todos , "todos render");
        var todosMap= this.state.todos.map((item,index)=>{
            return<div className='usrsMapping' key={index}>
                <p>Todos - User {item.id}</p>
                <input type='button' value='Add'/>
                <div>
                    <Todo userId={item.id} title={item.title} completed={item.completed} onUpdate={this.updateUser} onDelete={this.deleteUser}/>
                </div>
            </div>
        });

        return (
            <div id="Todos">
                {todosMap}
            </div>
        );
    }


}


const mapStateToProps = (state)=> {
    console.log("state todos",state);    return {
        data: state
    }
};


export default connect(mapStateToProps)(Todos);
