import React, { Component } from 'react';
import axios from "axios";


class Todo extends Component {
    constructor(props){
        super(props);
        this.state={ userId:this.props.userId, title:this.props.title, completed:this.props.completed};
    }

    static getDerivedStateFromProps(nextProps,nextState){
        console.log("user-getDerivedStateFromProps");
            return{ userId:nextProps.userId, title:nextProps.title, completed:nextProps.completed}
    }

    markAsCompleted =(x)=>{
        this.props.isCompleted(x);
        console.log("in markAsCompleted",x);
    };


    render() {
        return (
            <div id="Todo">
                <div>
                    <p><b>Title:</b> {this.state.title}</p>
                    <p><b>Completed:</b> { this.state.completed ?  "true" : "false" }</p>
                </div>
                <div>
                    { this.state.completed ?  <input  type='button' className='user-btn btn-completed' value='Mark Completed' onClick={this.markAsCompleted}/> : null }
                </div>
            </div>
        );
    }
}

export default Todo;
