import React, { Component } from 'react';
import axios from "axios";



class User extends Component {
    constructor(props){
        super(props);

        this.state={ id : this.props.id, name: this.props.name, email: this.props.email, street:this.props.street, city:this.props.city, zipcode:this.props.zipcode};
    }


    componentDidMount(){
        console.log(this.state.id);
        console.log(this.state.name);
        console.log(this.state.email);
        console.log(this.state.street);
    }

    active=(e)=>{
        e.preventDefault();
        console.log(e.currentTarget);
        console.log(e.target);
        if( this.state.active.length <= 0){
            this.setState({active:"active"});
        }else{
            this.setState({active:""});
        }
    };

    showOtherDataOnHover=(e)=>{
        console.log(this.usersMaps);

        /*     var element = this.state.users[index];*/
        var element1 = e.target;
        var element2 = element1.closest('#otherData');
        /*     console.log(element, "element");*/
        console.log(element1, "element1");
        console.log(element2, "element2");
        this.setState({active:"active"});
    };


    render() {
        return (
            <div id="User">
                <div ref={`user-${this.state.id}`}>
                    <p>ID: {this.state.id}</p>
                    Name: <input type='text' value={this.state.name}/><br/>
                    Email: <input type='text' value={this.state.email}/><br/>
                    <input  type='button' className='user-btn btn-other' value='Other Data' onMouseEnter={this.showOtherDataOnHover} onClick={this.active}  />
                    <div id='otherData' className={this.state.active}>
                        Street: <input type='text' value={this.state.street}/><br/>
                        City: <input type='text' value={this.state.city}/><br/>
                        Zipcode: <input type='text' value={this.state.zipcode}/><br/>
                    </div>

                    <input  type='button' className='user-btn btn-update' value='Update'/>
                    <input  type='button' className='user-btn btn-delete' value='Delete'/>
                </div>
            </div>
        );
    }
}

export default User;
