import React, { Component } from 'react';
import './dynamicRender.css';

class Repeater extends Component {

    constructor(props){
        super(props);
        this.state = {text:"",names:[
            {name:"ron",age:"30"},
            {name:"ron",age:"30"},
            {name:"ron",age:"30"},
            {name:"ron",age:"30"}
            ]};
    };


    addName=()=>{
      var arr = this.state.names;
      arr.push({name:"yoav" , age:"20"});
      this.setState({name:arr});
    };

    countText=(e)=>{
        var text = e.target.value;
        this.setState({text:text})
    };

    showChosenColor=()=>{
        console.log('in');


    };

    render() {

        var names = this.state.names.map((x , index)=>{
        return <li key={index}>{x.name} , {x.age}</li>
        });

        var names2 = this.state.names.map((x , index)=>{
        return <tr key={index}>
            <td>{x.name}</td>
            <td>{x.age}</td>
            </tr>
        });

        var text = this.state.text;
        var textStyle;

        if(this.state.text.length > 5){
            textStyle = "redTextBox";
        }else{
            textStyle = "blueTextBox";
        }

        return (
            <div className="dynamicRender">
                <ul>
                    {names}
                </ul>
                <input type='button' value="add name" onClick={this.addName}/>
                <table style={{border:"1px solid"}}>
                    <tbody>{names2}</tbody>
                </table>
                <input type="text" className={textStyle} onChange={this.countText}/>
                <input type="checkbox" name="red" onClick={this.showChosenColor}/>
             {/*   <input type="checkbox" name="green" onClick={}/>
                <input type="checkbox" name="blue" onClick={}/>*/}
            </div>
        );
    }
}

export default Repeater;
