import React, { Component } from 'react';
import './posts.css';
import DAL from "../../DALUtils";
import {connect} from 'react-redux';
import Post from "./Post";


class Posts extends Component {
    constructor(props){
        super(props);
        this.state={posts:[],clickedUserId:"",history:this.props.history};
    }

    static getDerivedStateFromProps(nextProps,nextState){
        console.log("Post-sgetDerivedStateFromProps");
        console.log("nextProps",nextProps);
        console.log("nextState",nextState);
        return{clickedUserId: nextProps.clickedUserId ,posts:nextProps.data.posts}
    }

    shouldComponentUpdate(){
        console.log("in shouldComponentUpdate");
        console.log("this.state.urlPath",this.state.urlPath);
        if(this.state.posts.length > 0){
            return false
        }return true
    }
    componentDidMount(){
        DAL.getData('https://jsonplaceholder.typicode.com/posts').
        then(res => this.props.dispatch({type:'GET-ALL-POSTS' , posts:res.data ,clickedUserId:this.state.clickedUserId})).
        catch(err => console.log(err));
    }

    pushToURL=()=>{
        this.props.history.push(`/tasks/${this.state.clickedUserId}/addPost`);
    };

    render() {
        var clicked = this.state.clickedUserId;
        var postsMap= this.state.posts.filter(x=>x.userId ==clicked).map((item,index)=>{
            return<div className='postMapping' key={index}>
                <div>
                    <Post title={item.title} body={item.body}/>
                </div>
            </div>
        });

        return (
            <div id="Posts">
                <div className='headlineDiv'>
                    <p>Posts - User {clicked}</p>
                    <input type='button' value='Add' className="add-btn" onClick={this.pushToURL}/>
                </div>
                {postsMap}
            </div>
        );
    }


}


const mapStateToProps = (state)=> {
    console.log("state Posts",state);
    return {
        data: state
    }
};


export default connect(mapStateToProps)(Posts);
