import React, { Component } from 'react';
import './users.css';
import User from "./User";
import DAL from "../DALUtils";
import {connect} from 'react-redux';

class Users extends Component {
    constructor(props){
        super(props);
        this.state={users:[],activeUser:""};
    }

    componentDidMount(){
        this.props.dispatch({type:'GET-USERS'})
    }


    static getDerivedStateFromProps(nextProps,nextState){
        console.log("UsersgetDerivedStateFromProps");
        console.log("UsersgetDerivedStateFromProps nextProps.data.activeUser",nextState);
        console.log("UsersgetDerivedStateFromProps nextProps.data.activeUser",nextState.activeUser);
        return{users:nextProps.data.users,activeUser:nextState.activeUser}
    }


    updateUser =(newId,newName,newEmail,newStreet,newCity,newZipcode)=>{
        var newUsers = this.state.users;
        var thisUserId = newId;
        var NewUserName = newName;
        var NewUserEmail = newEmail;
        var NewUserStreet = newStreet;
        var NewUserCity = newCity;
        var NewUserZipcode = newZipcode;

        for (var i in newUsers) {
            if (newUsers[i].id == thisUserId) {
                newUsers[i].name = NewUserName;
                newUsers[i].email = NewUserEmail;
                newUsers[i].address.street = NewUserStreet;
                newUsers[i].address.city = NewUserCity;
                newUsers[i].address.zipcode = NewUserZipcode;
                break; //Stop this loop, we found it!
            }
        }

        this.setState({users:newUsers});

    };

    deleteUser=(userId)=>{

        var userId = userId;
        var usersArr= this.state.users;
        var found = usersArr.find(function(x) {
            return x.id === userId ;
        });
        var index2 = usersArr.indexOf(found);
        usersArr.splice(index2, 1);
        this.setState({users:usersArr});
    };

    userActive=(x)=>{
        var userSelected = x;
        this.setState({activeUser:userSelected});
    };

    render() {
        console.log("in Render - activeUser" ,this.state.activeUser );
        console.log("this.state.users" ,this.state.users );


        var usersMap= this.state.users.map((item,index)=>{
            return<div className={`usrsMapping ${this.state.activeUser === item.id ? 'grey-bg' : 'ddd'}`}  key={index}>
                <User userId={item.id} name={item.name} email={item.email}
                      street={item.address.street} city={item.address.city} zipcode={item.address.zipcode}
                      onUpdate={this.updateUser} onDelete={this.deleteUser} onActiveUser={this.userActive}
                />
            </div>
        });

        return (
            <div id="Users">
                {usersMap}
            </div>
        );
    }


}


const mapStateToProps = (state)=> {
    console.log("state users!!!!!!!",state);
    return {
        data: state
    }
};


export default connect(mapStateToProps)(Users);
