const mainReducer = (
    state = {counter:0 ,users:[], todos:[]},  actionData) =>
{
    switch (actionData.type)
    {
        case 'INCREMENT':
        {
            return{counter:state.counter + actionData.delta}
        }
        case  'DECREMENT' :
        {
            return{counter:state.counter - actionData.delta}
        }
        case 'ADD-USER':
        {
            var arr = state.userArr;
            arr.push(actionData.user);
            return{userArr:arr}
        }
        case 'DELETE':{
            var arr = state.users;
            arr.push(actionData.user);
            return{users:arr}
        }
        case 'GET-ALL-USERS':{
            console.log('actionData-GET-ALL-USERS',actionData);
            return Object.assign({},state,{
                state:[
                    ...state.users,
                    {users:actionData.users}
                ]
            });





        }
        case 'GET-ALL-TODOS':{
            console.log("actionData todos-GET-ALL-TODOS",actionData);
            return[
                ...state,
                {todos:actionData.todos}
            ]

        }
        default:
            return state
    }
};

export default mainReducer;
