import React, { Component } from 'react';
import './todos.css';
import DAL from "../../DALUtils";
import {connect} from 'react-redux';
import Todo from "./Todo";

class Todos extends Component {
    constructor(props){
        super(props);
        this.state={todos:[],clickedUserId:"" ,};
    }

    static getDerivedStateFromProps(nextProps,nextState){
        console.log("Todos-sgetDerivedStateFromProps");
        console.log("nextProps",nextProps);
        console.log("nextState",nextState);
        if(nextProps != nextState){
            console.log("nextProps clickedUserId -----",nextProps.clickedUserId);
            return{clickedUserId: nextProps.clickedUserId , todos:nextProps.data.todos}
        }
    }

    componentDidUpdate(){
        return true;
    }

    componentDidMount(){
        console.log("this.state.clickedUserId DID" ,this.state.clickedUserId);
        DAL.getUsers('https://jsonplaceholder.typicode.com/todos').
        then(res => this.props.dispatch({type:'GET-ALL-TODOS' , todos:res.data , clickedUserId:this.state.clickedUserId}), console.log("res.data DID" , res.data)).
        catch(err => console.log(err));
    }



    render() {
        console.log("this.state.clickedUserId RENDAER" ,this.state.clickedUserId);
        var todosMap= this.state.todos.map((item,index)=>{
            return<div className='todoMapping' key={index}>
                <div className='headlineDiv'>
                    <p>Todos - User {item.userId}</p>
                    <input type='button' value='Add' className='add-btn'/>
                </div>
                <div>
                    <Todo userId={item.userId} title={item.title} completed={item.completed} />
                </div>
            </div>
        });

        return (
            <div id="Todos">
                {todosMap}
            </div>
        );
    }


}


const mapStateToProps = (state)=> {
    console.log("state todos",state);
    return {
        data: state
    }
};


export default connect(mapStateToProps)(Todos);
