import React, { Component } from 'react';


class DynamicRender extends Component {

    constructor(props){
        super(props);
        this.state = {isRed:false};
    };

    changeColor=()=>{
        this.setState((currentState)=>{
            return {isRed: !currentState.isRed}
        })
    };


    render() {
        return (
            <div className="dynamicRender">
                <h1>hello with color</h1>
                <input type="button" value={"change color"} onClick={this.changeColor}/>
            </div>
        );
    }
}

export default DynamicRender;
