import React, { Component } from 'react';

class ChildComp extends Component {
    constructor(props){
        super(props);
        this.state={name:"", age:"" , city:"" , adult:false}
    }

    setName = (e)=>{
        var name = e.target.value;
        this.setState({name:name});
    };

    setAge = (e)=>{
        var age = e.target.value;
        this.setState({age:age});
    };

    setCity = (e)=>{
        var city = e.target.value;
        this.setState({city:city});
    };

    setAdult = (prevState)=>{
        this.setState((prevState)=>{
            return {adult: !prevState.adult}
        });
    };

    childData=()=>{
        this.props.getChildProps([this.state.age , this.state.name , this.state.city , this.state.adult ])
    };

    render() {
        return (
            <div className={'personalData'}>
                <h1>im child</h1>
                <p>{this.props.dataFromParennt}</p>
                <p>text from parent comp -->{this.props.textFromParent}</p>
                name<input type="text" onChange={this.setName}/><br/>
                age<input type="number" onChange={this.setAge}/><br/>
                city<input type="text" onChange={this.setCity}/><br/>
                is adult<input type="checkbox" onChange={this.setAdult}/><br/>
                <input type="button" value="Add" onClick={this.childData}/>
            </div>
        );
    }
}

export default ChildComp;
