import React, { Component } from 'react';
import axios from "axios";
import {Link} from "react-router-dom";


class Contact extends Component {

    constructor(props){
        super(props);
        this.state={idVal:'' , linkUserId:'' , userData:{} ,  userAddress:[]}
    }

    static getDerivedStateFromProps(nextProps){
        return {idVal: nextProps.match.params.ID , linkUserId: nextProps.match.params.ID }
    }

    componentDidMount(){
        axios.get('https://jsonplaceholder.typicode.com/users/'+this.state.linkUserId).
        then(res => this.setState({userData : res.data})
        ).catch(err => console.log(err));

    }

    componentDidUpdate(){
        return true;
    }

    back=()=>{
        this.props.history.push('/about');
    };

    render() {

        /*var newUserAddress = this.state.userAdress.map( (item,index) =>
        {
            return <p key={index}>{item.title}</p>
        });*/


        return (
            <div className="">
                <p>contact page</p><br/>
                ID:{this.state.idVal}<br/>
                name:{this.state.userData.name}<br/>
                email:{this.state.userData.email}<br/>
                {/*
                address:{newUserAddress}<br/>
                */}

                <input type='button' value='back' onCliclk={this.back}/>
            </div>
        );
    }
}

export default Contact;
