import React, { Component } from 'react';
import axios from "axios";
import './user.css';


class Users extends Component {
    constructor(props){
        super(props);
        this.state={users:[],active:"",userMaps:[],};
    }


    componentDidMount(){
        axios.get('https://jsonplaceholder.typicode.com/users').
        then(res => this.setState({users : res.data})).
        catch(err => console.log(err));


    }

    active=(e)=>{
        e.preventDefault();
        console.log(e.currentTarget);
        console.log(e.target);
        if( this.state.active.length <= 0){
            this.setState({active:"active"});
        }else{
            this.setState({active:""});
        }
    };

    showOtherDataOnHover=(e)=>{
       console.log(this.usersMaps);

/*     var element = this.state.users[index];*/
     var element1 = e.target;
     var element2 = expect(element.closest('#otherData')).to.have.lengthOf(1);
     console.log(element, "element");
     console.log(element1, "element1");
     console.log(element1, "element2");
        this.setState({active:"active"});
    };


    render() {
        var usersMaps= this.state.users.map((item,index)=>{
            return<div key={index} ref={`user-${item.id}`}>
                <p>ID: {item.id}</p>
                Name: <input type='text' value={item.name}/><br/>
                Email: <input type='text' value={item.email}/><br/>
                <input  type='button' className='user-btn btn-other' value='Other Data' onMouseEnter={this.showOtherDataOnHover} onClick={this.active}  />
                {this.active &&
                <div id='otherData' className={this.state.active}>
                    Street: <input type='text' value={item.address.street}/><br/>
                    City: <input type='text' value={item.address.city}/><br/>
                    Zipcode: <input type='text' value={item.address.zipcode}/><br/>
                </div>
                }
                <input  type='button' className='user-btn btn-update' value='Update'/>
                <input  type='button' className='user-btn btn-delete' value='Delete'/>
            </div>
        });

        return (
            <div id="Users">
                {usersMaps}

            </div>
        );
    }
}

export default Users;
