import React, { Component } from 'react';
import Child from './StatelessComponent'

class Parent extends Component {

    constructor(props){
        super(props);
        this.state={data:'hello'}
    }

    changeData=()=>{
        this.setState({data:'bey bey'})
    };

    render() {

        return (
            <div className="">
                <h2>Parent</h2>
                <p>{this.state.data}</p>
                <input type='button' value='change data' onClick={this.changeData}/>
                <Child/>
            </div>
        );
    }}



export default Parent;
