import React, { Component } from 'react';
import "./MainTasks.css"
import Todos from "./Todos/Todos";
import Posts from "./Posts/Posts";
import {Switch,Route,Link} from 'react-router-dom';
import DAL from "../DALUtils";

class MainTasks extends Component {
    constructor(props){
        super(props);
        this.state={clickedUserId:'',history:''}
    }

    static getDerivedStateFromProps(nextProps,nextState){
        console.log("main Tasks-sgetDerivedStateFromProps");
        console.log("nextProps.match.params.ID --",nextProps.match.params.ID);
        console.log("nextProps.match.params",nextProps.history);

        return{clickedUserId: nextProps.match.params.ID ,history:nextProps.history}
    }

    componentDidMount(){DAL.getData('https://jsonplaceholder.typicode.com/todos').
    then(res => this.props.dispatch({type:'GET-ALL-TODOS' , todos:res.data ,todosDone:res.data , clickedUserId:this.state.clickedUserId,})).
    catch(err => console.log(err));}

    goBack(){
        this.props.history.push('/');
    }

    render() {
        console.log("this.state.clickedUserId in TODOS", this.state.clickedUserId);
        return (
            <div id="MainTasks" className="MainTasksClass">
                <Link to={`/`} className='goBack'/>
                    <Todos clickedUserId={this.state.clickedUserId}/>
                    <Posts clickedUserId={this.state.clickedUserId}/>
            </div>
        );
    }
}

export default MainTasks;
