import React, { Component } from 'react';
import axios from "axios";
import {Link, Route, Switch} from "react-router-dom";
import ContactEmail from "./ContactEmail";
import ContactPhone from "./ContactPhone";



class Contact extends Component {

    constructor(props){
        super(props);
        this.state={idVal:'' , linkUserId:'' , userData:{} ,  userAddress:[] , linkName2:''}
    }

    static getDerivedStateFromProps(nextProps){
        return {idVal: nextProps.match.params.ID , linkUserId: nextProps.match.params.ID , linkName2: nextProps.match.params.Name}
    }

    componentDidMount(){
        axios.get('https://jsonplaceholder.typicode.com/users/'+this.state.linkUserId).
        then(res => this.setState({userData : res.data})
        ).catch(err => console.log(err));


    }

    componentDidUpdate(){
        return true;
    }

    back=()=>{
        console.log(this.props.history , 'rout history');
        this.props.history.push('/about');
    };

    render() {


        return (
            <div className="">
                <p>contact page</p><br/>
                ID:{this.state.idVal}<br/>
                name:{this.state.userData.name}<br/>
                name2:{this.state.linkName2}<br/>

                <nav>
                    <li><Link to={`${this.props.match.url}/email`}>get our mail by this id</Link></li>
                    <li><Link to={`${this.props.match.url}/phone`}>get our phone this id</Link></li>
                </nav>
                <input type='button' value='Back' onClick={this.back}/>
                <Switch>
                    <Route path='/contact/:ID/email' component={ContactEmail}/>
                    <Route path='/contact/:ID/Phone' component={ContactPhone}/>
                </Switch>

            </div>
        );
    }
}

export default Contact;
