const mainReducer = (
    state = {users:[], todos:[],posts:[],clickedUserId:""},  actionData) =>
{
    switch (actionData.type)
    {
        case 'GET-ALL-USERS':{
            console.log('actionData-GET-ALL-USERS',actionData);
            return Object.assign({},state,
                ...state.users,
                {users:actionData.users}
            );
        }
        case 'GET-ALL-TODOS':{
            console.log("actionData todos-GET-ALL-TODOS",actionData);
            var clickedUserId = actionData.clickedUserId;
            var todosByIdArr= actionData.todos;
            var todosMap= todosByIdArr.map((item,index)=>{
                if(clickedUserId != todosByIdArr[index]){
                    console.log("index" , todosByIdArr[index]);
                    console.log(clickedUserId,'clickedUserId');
                    todosByIdArr.splice(index, 1);
                    console.log("todosMap",todosMap)
                }

                return todosMap;
            });
            return Object.assign({},state,
                ...state.todos,

                {todos:todosMap}
            );

        }
        case 'GET-ALL-POSTS':{
            console.log("actionData posts-GET-ALL-TODOS",actionData);
            return Object.assign({},state,
                ...state.posts,
                {posts:actionData.posts}
            );

        }
        case 'ADD-USER':
        {
            var arr = state.userArr;
            arr.push(actionData.user);
            return{userArr:arr}
        }
        case 'DELETE':{
            var arr = state.users;
            arr.push(actionData.user);
            return{users:arr}
        }

        default:
            return state
    }
};

export default mainReducer;
