import React, { Component } from 'react';
import './users.css';
import User from "./User";
import DAL from "../DALUtils";
import {connect} from 'react-redux';

class Users extends Component {
    constructor(props){
        super(props);
        this.state={users:[],activeUser:"" ,clickedUserId:"",todos:[]};
    }

    componentDidMount(){
        this.props.dispatch({type:'GET-USERS'});
    }


    static getDerivedStateFromProps(nextProps,nextState){
        console.log("UsersgetDerivedStateFromProps");
        console.log("UsersgetDerived nextProps.data.activeUser",nextState.activeUser);
        console.log("UsersgetDerived clickedUserId",nextState.clickedUserId);
        console.log("UsersgetDerived nextProps.data.users",nextProps.data.users);
        console.log("UsersgetDerived nextState.users",nextState.users);

        return{users:nextProps.data.users,activeUser:nextState.activeUser,todos:nextProps.data.todos}
    }


    updateUser =(newId,newName,newEmail,newStreet,newCity,newZipcode)=>{
        var newUsers = this.state.users;
        var thisUserId = newId;
        var NewUserName = newName;
        var NewUserEmail = newEmail;
        var NewUserStreet = newStreet;
        var NewUserCity = newCity;
        var NewUserZipcode = newZipcode;

        for (var i in newUsers) {
            if (newUsers[i].id == thisUserId) {
                newUsers[i].name = NewUserName;
                newUsers[i].email = NewUserEmail;
                newUsers[i].address.street = NewUserStreet;
                newUsers[i].address.city = NewUserCity;
                newUsers[i].address.zipcode = NewUserZipcode;
                break; //Stop this loop, we found it!
            }
        }

        this.setState({users:newUsers});

    };

    deleteUser=(userId)=>{

        var userId = userId;
        var usersArr= this.state.users;
        var found = usersArr.find(function(x) {
            return x.id === userId ;
        });
        var index2 = usersArr.indexOf(found);
        usersArr.splice(index2, 1);
        this.setState({users:usersArr});
    };

    userActive=(x)=>{
        var userSelected = x;
        this.setState({activeUser:userSelected});
    };

    render() {
        console.log("in Render - activeUser" ,this.state.activeUser );
        console.log("in Render - this.state.users" ,this.state.users );
        console.log("in Render - this.state.todos",this.state.todos);
        var todos = this.state.todos;



        var usersMap= this.state.users.map((item,index)=>{
            console.log("user.id",item.id);
                var userItem = item;
                console.log("userItem",userItem);
            var thisIdArr = todos.filter(x=>x.userId === item.id);

            console.log("thisIdArr",thisIdArr);

            for(var i = 0; i< thisIdArr.length; i++){
                console.log("thisIdArr[i]",thisIdArr[i]);
                console.log("thisIdArr[i].completed",thisIdArr[i].completed);
                if(thisIdArr[i].completed === false){
                    console.log("todo item",item);
                    console.log("userItem",userItem);
                    userItem["completed"]=false;
                    break;
                }else{
                    console.log("adding completed to username: ",userItem);
                    userItem["completed"]=true;
                }

            }



            return<div className={`usrsMapping ${this.state.activeUser === item.id ? 'grey-bg' : ''}`}  key={index}>
                <User userId={item.id} name={item.name} email={item.email}
                      street={item.address.street} completed={item.completed}
                      city={item.address.city} zipcode={item.address.zipcode}
                      onUpdate={this.updateUser} onDelete={this.deleteUser} onActiveUser={this.userActive}
                />
            </div>
        });

        return (
            <div id="Users">
                {usersMap}
            </div>
        );
    }


}


const mapStateToProps = (state)=> {
    console.log("state users!!!!!!!",state);
    return {
        data: state
    }
};


export default connect(mapStateToProps)(Users);
