import React, { Component } from 'react';
import "./MainTasks.css"
import Todos from "./Todos/Todos";
import Posts from "./Posts/Posts";
import {Switch,Route,Link} from 'react-router-dom';

class MainTasks extends Component {
    constructor(props){
        super(props);
        this.state={clickedUserId:'',history:'',addClicked:""}
    }

    static getDerivedStateFromProps(nextProps,nextState){
        console.log("main Tasks-sgetDerivedStateFromProps");
        console.log("nextProps",nextProps);
        console.log("nextProps.path",nextProps.match.path);
        console.log("nextProps.match.params.ID --",nextProps.match.params.ID);
        console.log("nextProps.match.params",nextProps.history.location.pathname);

        return{clickedUserId:nextProps.match.params.ID ,history:nextProps.history , addClicked:nextProps.history.location.pathname, urlID:nextProps.match.params.ID}
    }

    componentDidMount(){console.log("gsdgsdgdsgsdgsdg")}


    render() {
        console.log("this.state.clickedUserId in TODOS", this.state.clickedUserId);
        console.log("this.state.addClicked", this.state.addClicked);
        return (
            <div id="MainTasks" className="MainTasksClass">
                <Link to={`/`} className='goBack' />
                {this.state.addClicked === `/tasks/${this.state.urlID}/addTodo` ? <div>title: <input type="text"/><br/><input type="button" value="Cancel"/><input type="button" value="Add"/></div>
                    : <Todos clickedUserId={this.state.clickedUserId} history={this.props.history}/>}
                {this.state.addClicked+"/addPost" === "/addPost" ?  <div>title: <input type="text"/><br/>body: <input type="text"/><br/><input type="button" value="Cancel"/><input type="button" value="Add"/></div>
                    : <Posts clickedUserId={this.state.clickedUserId} history={this.props.history}/>}

            </div>
        );
    }
}

export default MainTasks;
