    super(props);
    this.state= {data: "hello"}
  }

    changeChildeData=()=>{
      this.setState({data:"no longer hello"});
    };

  render() {
    return (
      <div className={''}>
          <p>Parent</p>
        <input type="button" onClick={this.changeChildeData} value="change child data"/>
        <ChildComp dataFromParennt={this.state.data}/>
      </div>
    );
  }
}

