import React, { Component } from 'react';
import DAL from '../DALUtils'

class ChildCompLifecycleDemo extends Component {

    constructor(props){
        super(props);
        this.state = {id:'', childId:0 , newName:''};
        console.log('constructor');
    }
    static getDerivedStateFromProps(props){
        console.log('getDerivedStateFromProps');
        return {parentId:props.childId}
        /*return null*/
    }

    componentDidMount(){
        console.log('componentDidMount');
        console.log(this.state.childId , "childId");
        DAL.getData(`https://jsonplaceholder.typicode.com/users${this.state.id}`).then(
            res => this.setState({newName : res.data.name})
        )
    }

    shouldComponentUpdate(nextProps , nextState){
        console.log('shouldComponentUpdate');
        return true;
    }

    componentDidUpdate(){
        console.log('componentDidUpdate');
        return true;
    }

    render() {




        return (
            <div>

            </div>
        );
    }
}

export default ChildCompLifecycleDemo;
