const mainReducer = (
    state = {counter:0 ,users:[], todos:[]},  actionData) =>
{
    switch (actionData.type)
    {
        case 'INCREMENT':
        {
            return{counter:state.counter + actionData.delta}
        }
        case  'DECREMENT' :
        {
            return{counter:state.counter - actionData.delta}
        }
        case 'ADD-USER':
        {
            var arr = state.userArr;
            arr.push(actionData.user);
            return{userArr:arr}
        }
        case 'DELETE':{
            var arr = state.users;
            arr.push(actionData.user);
            return{users:arr}
        }
        case 'GET-ALL-USERS':{
            return{users:actionData.users}
        }
        case 'GET-ALL-TODOS':{
            console.log("actionData todos",actionData);
            return{actionData,todos:actionData.todos}
        }
        default:
            return state
    }
};

export default mainReducer;
