import React, { Component } from 'react';


class PersonComp extends Component {

    constructor(props){
        super(props);
        this.state = {name: "guffi" , age:'5' , counter:"" , dataText:"" , number:"4" ,total: "", newNumber:"" ,
            firstText:"4" , secText:"ff" , checkedBox:true};
    };

    changeName = () =>{
        this.setState(function (currentState , props) {
            return {name:'daniel',age:'4',counter: currentState.counter +1}
        },() =>{console.log(this.state.name)}
    )};

    getNumber =(e)=>{
       var num = e.target.value;
        this.setState({newNumber: num})
    };

    changeTotal=()=>{
        var total = parseInt(this.state.total);
        var newNumber = parseInt(this.state.current);
        total = total + newNumber;
        this.setState({total: total})
    };

    getValueOnChange=(e)=>{
        var temp = e.target.value;
        this.setState(()=>{
            return {firstText: temp}
        },()=>{
            if(this.state.firstText.length > 4){
                this.swapFunction();
            }
        });
    };


    getValueOnChange2=(e)=>{
        var temp = e.target.value;
        this.setState(()=>{
            return {secText: temp}
        },()=>{
            if(this.state.secText.length > 4){
                this.swapFunction();
            }
        });

    };

    swapFunction=()=>{
        var temp = this.state.firstText;
        var temp2 = this.state.secText;
    this.setState({firstText: temp2 , secText:temp})
    };

    setCheckBox=()=>{
        this.setState(function (currentState) {
            return { checkedBox : !currentState.checkbox}
        });
    };

    setCheck=(e)=>{};

    render() {
        return (
            <div className="setState">
                <span>my name is: {this.state.name}</span>
                <span> and my age is: {this.state.age}</span><br/>
            <input type='button' value='change name' onClick={this.changeName}/>
                <br/>
                <span>my total is:{this.state.number}</span><br/>
            <input type='number' onChange={this.getNumber} />
            <input type='button' value="add" onClick={this.changeTotal}/>
            <br/>

            <input type='text' onChange={this.getValueOnChange}/><br/>
                <input type='text' onChange={this.getValueOnChange2}/><br/>
                <input type='button' value="swap" onClick={this.swapFunction}/><br/>
            <span>first text box value is {this.state.firstText}</span><br/>
            <span>sec text box value is {this.state.secText}</span>
            <br/>
            <input type="radio" name="1" onClick={this.setCheckBox}/><br/>
            <input type="radio" name="1" onClick={this.setCheckBox}/><br/>
            <input type="checkbox" onChange={this.setCheck} checked={this.state.checkedBox}/><br/>
            </div>
        );
    }
}

export default PersonComp;
