import React, { Component } from 'react';
import './dynamicRender.css';

class DynamicRender extends Component {

    constructor(props){
        super(props);
        this.state = {isRed:false};
    };

    changeColor=()=>{
        this.setState((currentState)=>{
            return {isRed: !currentState.isRed}
        })
    };


    render() {

        var headerStyle;
        if(this.state.isRed == true){
            headerStyle = "redHeader";
        }else{
            headerStyle = "blueHeader";
        }


        return (
            <div className="dynamicRender">
                <h1>hello with color</h1>
                <input className={headerStyle} type="button" value={"change color"} onClick={this.changeColor}/>
            </div>
        );
    }
}

export default DynamicRender;
