import React, { Component } from 'react';
import axios from "axios";
import {Link} from "react-router-dom";
import './user.css';


class Users extends Component {
    constructor(props){
        super(props);
        this.state={users:[],active:""};
    }


    componentDidMount(){
        axios.get('https://jsonplaceholder.typicode.com/users').
        then(res => this.setState({users : res.data})).
        catch(err => console.log(err));
    }

    active=()=>{
        if( this.state.active.length <= 0){
            this.setState({active:"active"});
        }else{
            this.setState({active:""});
        }
    };

    showOnHover=()=>{
        this.setState({active:"active"});
    };


    render() {
        var users= this.state.users.map((item,index)=>{
            return<div key={index}>
                <p>ID: {item.id}</p>
                Name: <input type='text' value={item.name}/><br/>
                Email: <input type='text' value={item.email}/><br/>
                <input  type='button' className='user-btn btn-other' value='Other Data' onMouseEnter={this.showOnHover} onClick={this.active}  />
                <div id='otherData' className={this.state.active}>
                    Street: <input type='text' value={item.address.street}/><br/>
                    City: <input type='text' value={item.address.city}/><br/>
                    Zipcode: <input type='text' value={item.address.zipcode}/><br/>
                </div>
                <input  type='button' className='user-btn btn-update' value='Update'/>
                <input  type='button' className='user-btn btn-delete' value='Delete'/>
            </div>
        });


        return (
            <div id="Users">
                {users}
            </div>
        );
    }
}

export default Users;
