import React, { Component } from 'react';
import axios from "axios";
import {Link, Route, Switch} from "react-router-dom";
import ContactEmail from "./ContactEmail";
import ContactPhone from "./ContactPhone";



class StageThree extends Component {

    constructor(props){
        super(props);
        this.state={city:''}
    }

    componentDidMount(){}

    componentDidUpdate(){
        return true;
    }

    getCity=(e)=>{
      var city = e.target.value;
      this.setState({city:city})
    };
    saveStorage=()=>{
        var a = {};
        // Parse the serialized data back into an aray of objects
        a = JSON.parse(localStorage.getItem('myData'));
        // Push the new data (whether it be an object or anything else) onto the array
        a.push(this.state.city);
        // Alert the array value
        alert(a);  // Should be something like [Object array]
        // Re-serialize the array back into a string and store it in localStorage
        localStorage.setItem('myData', JSON.stringify(a));

        /*---console the data in the localStorage---*/
         var getDataFromStorage = localStorage.getItem('myData');
         console.log('getDataFromStorage:', JSON.parse(getDataFromStorage));
    };

    render() {

        return (
            <div className="">
                <p>Chose City</p><br/>
                <select name='city' onChange={this.getCity}>
                    <option value="volvo">Hifa</option>
                    <option value="saab">Tel Aviv</option>
                    <option value="mercedes">Hadera</option>
                </select>
                <input type='button' onClick={this.saveStorage}/>
            </div>
        );
    }
}

export default StageThree;
