import React, { Component } from 'react';
import ChildCompLifecycleDemo from "./ChildeCompLifecycleDemo";

class ParentCompLifecycleDemo extends Component {

  constructor(props){
      super(props);
      this.state = {id:'' , number:0 , parentId:0 , newParentId:0 , counter:0};
      console.log('constructor');
  }
  static getDerivedStateFromProps(props){
      console.log('getDerivedStateFromProps');
      return {id: props.id}
      /*return null*/
  }

  componentDidMount(){
      console.log('componentDidMount');
  }

  shouldComponentUpdate(nextProps , nextState){
      console.log('shouldComponentUpdate');

      if(this.state.number >= 5){
          return false
      }
      return true;
  }

    componentDidUpdate(){
        console.log('componentDidUpdate');
        return true;
    }

    pulseFunction =()=>{
      /*var newNumber = this.state.number + 1;
      this.setState({number:newNumber});*/
      this.setState(prevState =>{
          return{counter: prevState+1}
      })

    };

    chosenId=(e)=>{
        var chosenID = e.target.value;
        this.setState({
            parentId:chosenID
        });
    };
    setNewId=()=>{
       this.setState({
           newParentId:this.state.parentId
       })
    };

    render() {




        return (
          <div>
              {this.state.counter}<br/>
              <input type="button" value="+" onClick={this.pulseFunction}/>
          <br/>
              <input type="number" onChange={this.chosenId} />
              <input type="button" value='set new ID' onClick={this.setNewId} />

              <ChildCompLifecycleDemo childId={this.state.newParentId}/>
          </div>
        );
    }
}

export default ParentCompLifecycleDemo;
