import React, { Component } from 'react';
import axios from "axios";
import './users.css';
import User from "./User";


class Users extends Component {
    constructor(props){
        super(props);
        this.state={users:[]};
    }


    componentDidMount(){
        axios.get('https://jsonplaceholder.typicode.com/users').
        then(res => this.setState({users : res.data})).
        catch(err => console.log(err));


    }

    updateUser =(e)=>{
        console.log("in updatee user");
        console.log(e.target.value);
        console.log(this.state.users[this.props.thisUserId])
    };


    render() {
        var usersMap= this.state.users.map((item,index)=>{
            return<div  key={index}>
                <User id={item.id} name={item.name} email={item.email} street={item.address.street} city={item.address.city} zipcode={item.address.zipcode} onUpdate={this.updateUser}/>
            </div>
        });

        return (
            <div id="Users">
                {usersMap}
            </div>
        );
    }
}

export default Users;
