import React, { Component } from 'react';
import DAL from '../DALUtils'

class ChildCompLifecycleDemo extends Component {

    constructor(props){
        super(props);
        this.state = {id:'', childId:0 , user:{id:'' , name: ''},};
        console.log('constructor');
    }
    static getDerivedStateFromProps(props){
        console.log('getDerivedStateFromProps');
        return {childId:props.newParentId}
        /*return null*/
    }

    componentDidMount(){
        console.log('componentDidMount');

    }

    shouldComponentUpdate(nextProps , nextState){
        console.log('shouldComponentUpdate');
        return true;
    }

    componentDidUpdate(){
        console.log('componentDidUpdate');
        console.log(this.state.childId , "childId");
        if(this.state.childId != this.state.user.id){
            DAL.getData('https://jsonplaceholder.typicode.com/users' + this.state.childId).
            then(
                res => this.setState({user : res.data})
            );
        }

        return true;
    }

    render() {




        return (
            <div>
                {this.state.user.name}
            </div>
        );
    }
}

export default ChildCompLifecycleDemo;
