import React, { Component } from 'react';


class StageThree extends Component {

    constructor(props){
        super(props);
        this.state={newObj:'Hifa',arr:[]}
    }

    componentDidMount(){
        var getDataFromStorage = localStorage.getItem('myData');
        var newstoragData = JSON.parse(getDataFromStorage);
        var arr=[newstoragData];
        this.setState({arr:arr});
    }

    componentDidUpdate(){
        return true;
    }

    getCity=(e)=>{
        var chosenCity = e.target.value;
        var newObj=chosenCity;
        this.setState({newObj:newObj});

    };

    saveStorage=()=>{
        console.log(this.state.newObj , 'my new obj');
        this.state.arr.push({city:this.state.newObj});
        console.log(this.state.arr, 'new arr');
        localStorage.setItem('myData',JSON.stringify(this.state.arr));

        /*---console new localStorage---*/
        /* var getData = localStorage.getItem('myData');
           console.log('new storage:', JSON.parse(getData));*/

        this.props.history.push('/stage4');

    };

    render() {

        return (
            <div className="">
                <p>Chose City</p><br/>
                <select name='city' onChange={this.getCity}>
                    <option value="Hifa">Hifa</option>
                    <option value="TLV">Tel Aviv</option>
                    <option value="Hadera">Hadera</option>
                </select>
                <br/>
                <input type='button' value='Next' onClick={this.saveStorage}/>
            </div>
        );
    }
}

export default StageThree;
