import React, { Component } from 'react';


class PersonComp extends Component {

    constructor(props){
        super(props);
        this.state = {name: "guffi" , age:'5' , counter:"" , dataText:""};
    };

    changeName = () =>{
        this.setState(function (currentState , props) {
            return {name:'daniel',age:'4',counter: currentState.counter +1}
        },() =>{console.log(this.state.name)}
    )};

    changeText =(e)=>{
        this.setState({dataText: e.target.value})
    };

    render() {
        return (
            <div className="App">
                <sapn>name is: {this.state.name}</sapn>
                <sapn>my age is: {this.state.age}</sapn>
            <input type='button' value='change name' onClick={this.changeName}/>
            <input type='text'  value='Change Text' onChange={this.changeText}/>
            
            </div>
        );
    }
}

export default PersonComp;
