import React, { Component } from 'react';
import './todos.css';
import DAL from "../../DALUtils";
import {connect} from 'react-redux';
import Todo from "./Todo";

class Todos extends Component {
    constructor(props){
        super(props);
        this.state={todos:[],clickedUserId:""};
    }

    static getDerivedStateFromProps(nextProps,nextState){
        console.log("Todos-sgetDerivedStateFromProps");
        if(nextProps != nextState){
            return{clickedUserId: nextProps.clickedUserId , todos:nextProps.data.todos}
        }else{
            console.log("elseee TODOSSS");
            console.log("nextState.todos",nextState.todos);
            return{clickedUserId: nextState.clickedUserId , todos:nextState.todos}
        }
    }

    componentDidMount(){
        console.log("this.state.clickedUserId DID" ,this.state.clickedUserId);
        DAL.getData('https://jsonplaceholder.typicode.com/todos').
        then(res => this.props.dispatch({type:'GET-ALL-TODOS' , todos:res.data , clickedUserId:this.state.clickedUserId})).
        catch(err => console.log(err));
    }

    completeTask=(x,y)=>{
        var newTodos = this.state.todos;
        var todoUserId = x;
        var todoId = y;

        console.log(x);
        console.log(y);

        for (var i in newTodos) {
            if (newTodos[i].userId == todoUserId && newTodos[i].id === todoId ) {
                newTodos[i].completed = true;
                break;
            }
        }
        console.log("new arr" , newTodos);
        this.setState({todos:newTodos});
        console.log("new State" , this.state.todos);
    };


    render() {
        var clicked = this.state.clickedUserId;
        var todosMap= this.state.todos.filter(x=>x.userId ==clicked).map((item,index)=>{
            return<div className='todoMapping' key={index}>
                <div>
                    <Todo userId={item.userId} id={item.id} title={item.title} completed={item.completed} isCompleted={this.completeTask} />
                </div>
            </div>
        });

        return (
            <div id="Todos">
                <div className='headlineDiv'>
                    <p>Todos - User {clicked}</p>
                    <input type='button' value='Add' className='add-btn'/>
                </div>
                {todosMap}
            </div>
        );
    }


}


const mapStateToProps = (state)=> {
    console.log("state todos",state);
    return {
        data: state
    }
};


export default connect(mapStateToProps)(Todos);
